        role: "user",
        text: content,
      });

      const allMessages = await storage.getSessionMessages(session.id);

      const conversationHistory = allMessages.map((msg) => ({
        role: msg.role === "ai" ? ("assistant" as const) : ("user" as const),
        content: msg.text,
      }));

      res.setHeader("Content-Type", "text/event-stream");
      res.setHeader("Cache-Control", "no-cache");
      res.setHeader("Connection", "keep-alive");

      console.log("[CHAT] Starting OpenAI stream...");
      console.log(
        "[CHAT] Conversation history length:",
        conversationHistory.length,
      );

      const stream = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          { role: "system", content: RIYA_SYSTEM_PROMPT },
          ...conversationHistory,
        ],
        max_tokens: 300,
        stream: true,
      });

      let fullResponse = "";
      let chunkCount = 0;

      for await (const chunk of stream) {
        const content = chunk.choices[0]?.delta?.content || "";
        if (content) {
          fullResponse += content;
          chunkCount++;
          res.write(`data: ${JSON.stringify({ content, done: false })}\n\n`);
        }
      }

      console.log(
        "[CHAT] Stream complete. Chunks:",
        chunkCount,
        "Total length:",
        fullResponse.length,
      );

      const aiMessage = await storage.createMessage({
        sessionId: session.id,
        userId: user.id,
        role: "ai",
        text:
          fullResponse ||
          "I'm sorry, I couldn't process that. Can you please try again?",
