import { useState } from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Check, Loader2 } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface PaywallSheetProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  messageCount: number;
}

// Declare Cashfree global type
declare global {
  interface Window {
    Cashfree: any;
  }
}

export function PaywallSheet({ open, onOpenChange, messageCount }: PaywallSheetProps) {
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();

  const createOrderMutation = useMutation({
    mutationFn: async (planType: 'daily' | 'weekly') => {
      const response = await apiRequest('POST', '/api/payment/create-order', { planType });
      return response.json();
    },
  });

  const handleSelectPlan = async (planType: 'daily' | 'weekly') => {
    try {
      setIsProcessing(true);

      // Create payment order
      const orderData = await createOrderMutation.mutateAsync(planType);

      if (!orderData.paymentSessionId) {
        throw new Error('Failed to create payment session');
      }

      // Initialize Cashfree SDK
      const cashfree = new window.Cashfree({
        mode: "sandbox" // Change to "production" for live payments
      });

      // Open checkout
      const checkoutOptions = {
        paymentSessionId: orderData.paymentSessionId,
        returnUrl: `${window.location.origin}/payment/callback?orderId=${orderData.orderId}`,
      };

      cashfree.checkout(checkoutOptions).then((result: any) => {
        if (result.error) {
          toast({
            title: "Payment Failed",
            description: result.error.message || "Something went wrong with the payment",
            variant: "destructive",
          });
          setIsProcessing(false);
          return;
        }

        if (result.redirect) {
          // Payment redirect - user will be redirected to callback page
          console.log("Payment redirect initiated");
          // Don't verify here - it will be done in the callback page
          setIsProcessing(false);
        }
      }).catch((error: any) => {
        console.error('Cashfree checkout error:', error);
        toast({
          title: "Payment Error",
          description: "Failed to open payment page. Please try again.",
          variant: "destructive",
        });
        setIsProcessing(false);
      });

    } catch (error: any) {
      console.error('Payment error:', error);
      toast({
        title: "Error",
        description: error.message || "Failed to initiate payment",
        variant: "destructive",
      });
      setIsProcessing(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader className="text-center space-y-3 pb-6">
          <DialogTitle className="text-2xl font-bold">
            You've Reached Your Free Limit
          </DialogTitle>
          <DialogDescription className="text-base">
            You've used {messageCount} messages. Upgrade to continue chatting with Riya!
          </DialogDescription>
        </DialogHeader>

        <div className="grid gap-4 py-6 max-w-2xl mx-auto">
          {/* Daily Plan */}
          <Card className="p-6 border-2 hover-elevate transition-all">
            <div className="flex flex-col gap-4">
              <div className="flex items-start justify-between">
                <div>
                  <h3 className="text-xl font-bold text-primary">Daily Pass</h3>
                  <p className="text-sm text-muted-foreground mt-1">Perfect for trying out</p>
                </div>
                <div className="text-right">
                  <p className="text-3xl font-bold">₹19</p>
                  <p className="text-sm text-muted-foreground">per day</p>
                </div>
              </div>

              <ul className="space-y-2 text-sm">
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Unlimited messages for 24 hours</span>
                </li>
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Unlimited voice calls</span>
                </li>
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Priority responses</span>
                </li>
              </ul>

              <Button 
                onClick={() => handleSelectPlan('daily')}
                className="w-full"
                data-testid="button-plan-daily"
                disabled={isProcessing}
              >
                {isProcessing ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Processing...
                  </>
                ) : (
                  'Get Daily Pass'
                )}
              </Button>
            </div>
          </Card>

          {/* Weekly Plan - Highlighted */}
          <Card className="p-6 border-2 border-primary bg-primary/5 hover-elevate transition-all relative">
            <div className="absolute -top-3 right-4 bg-primary text-primary-foreground px-3 py-1 rounded-full text-xs font-semibold">
              BEST VALUE
            </div>

            <div className="flex flex-col gap-4">
              <div className="flex items-start justify-between">
                <div>
                  <h3 className="text-xl font-bold text-primary">Weekly Pass</h3>
                  <p className="text-sm text-muted-foreground mt-1">Most popular choice</p>
                </div>
                <div className="text-right">
                  <p className="text-3xl font-bold">₹49</p>
                  <p className="text-sm text-muted-foreground">per week</p>
                  <p className="text-xs text-accent font-semibold mt-1">Save 65%</p>
                </div>
              </div>

              <ul className="space-y-2 text-sm">
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Unlimited messages for 7 days</span>
                </li>
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Unlimited voice calls</span>
                </li>
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Priority responses</span>
                </li>
                <li className="flex items-center gap-2">
                  <Check className="w-4 h-4 text-accent" />
                  <span>Early access to new features</span>
                </li>
              </ul>

              <Button 
                onClick={() => handleSelectPlan('weekly')}
                className="w-full"
                data-testid="button-plan-weekly"
                disabled={isProcessing}
              >
                {isProcessing ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Processing...
                  </>
                ) : (
                  'Get Weekly Pass'
                )}
              </Button>
            </div>
          </Card>

          <p className="text-center text-xs text-muted-foreground mt-4">
            Secure payment powered by Cashfree
          </p>
        </div>
      </DialogContent>
    </Dialog>
  );
}

