import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { ClipboardList, Loader2, LogOut } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/contexts/AuthContext";

interface ChatHeaderProps {
  sessionId?: string;
}

export function ChatHeader({ sessionId }: ChatHeaderProps) {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user, logout } = useAuth();

  const endSessionMutation = useMutation({
    mutationFn: async () => {
      if (!sessionId) throw new Error("No session ID");

      const response = await apiRequest("POST", "/api/session/end", {
        sessionId,
      });
      return response.json();
    },
    onSuccess: (data: { success: boolean; summary: any }) => {
      toast({
        title: "Session Ended",
        description: "Your conversation summary has been generated",
      });

      // Invalidate queries to refresh data
      queryClient.invalidateQueries({ queryKey: ["/api/summary"] });
      queryClient.invalidateQueries({ queryKey: ["/api/session"] });

      // Navigate to summary page
      setLocation("/summary");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to end session",
        variant: "destructive",
      });
    },
  });

  const handleSummaryClick = () => {
    if (sessionId) {
      endSessionMutation.mutate();
    } else {
      setLocation("/summary");
    }
  };

  const handleLogout = async () => {
    try {
      await apiRequest("POST", "/api/auth/logout", {});
      logout();
      setLocation("/login");
      toast({
        title: "Logged Out",
        description: "You have been successfully logged out",
      });
    } catch (error) {
      toast({
        title: "Logout Failed",
        description: "Failed to logout. Please try again.",
        variant: "destructive",
      });
    }
  };

  return (
    <header className="fixed top-0 left-0 right-0 h-16 bg-background/95 backdrop-blur-sm border-b border-border z-10">
      <div className="max-w-2xl mx-auto h-full flex items-center justify-between px-4">
        <h1 className="text-xl font-bold text-foreground" data-testid="text-header-title">
          Riya
        </h1>
        <div className="flex gap-2">
          <Button 
            variant="outline" 
            size="sm" 
            className="gap-2" 
            onClick={handleSummaryClick}
            disabled={endSessionMutation.isPending}
            data-testid="button-summary-tracker"
          >
            {endSessionMutation.isPending ? (
              <Loader2 className="w-4 h-4 animate-spin" />
            ) : (
              <ClipboardList className="w-4 h-4" />
            )}
            Summary Tracker
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleLogout}
            className="gap-2"
            data-testid="button-logout"
          >
            <LogOut className="w-4 h-4" />
            Logout
          </Button>
        </div>
      </div>
    </header>
  );
}

