client/src/components/ui/chart.tsx.content:
"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

client/src/hooks/useVoiceCall.ts.content:
import { useRef, useState, useCallback, useEffect } from 'react';

interface VoiceCallState {
  isConnected: boolean;
  isRecording: boolean;
  transcript: string;
  error: string | null;
}

export function useVoiceCall(sessionId: string | null) {
  const [state, setState] = useState<VoiceCallState>({
    isConnected: false,
    isRecording: false,
    transcript: '',
    error: null,
  });

  const sttWsRef = useRef<WebSocket | null>(null);
  const ttsWsRef = useRef<WebSocket | null>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const transcriptRef = useRef<string>('');

  const connect = useCallback(async () => {
    if (!sessionId) {
      console.log('[Voice] Cannot connect: no sessionId');
      return;
    }

    try {
      // Use wss:// for HTTPS, ws:// for HTTP
      const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
      const wsBaseUrl = `${protocol}//${window.location.host}/ws`;
      const sttUrl = `${wsBaseUrl}?type=stt&sessionId=${sessionId}`;
      console.log('[Voice] Window location:', {
        protocol: window.location.protocol,
        host: window.location.host,
        wsProtocol: protocol,
        wsBaseUrl,
        sttUrl
      });

      // Connect STT WebSocket
      console.log('[Voice] Creating STT WebSocket with URL:', sttUrl);
      const sttWs = new WebSocket(sttUrl);
      sttWsRef.current = sttWs;

      console.log('[Voice] STT WebSocket created, readyState:', sttWs.readyState);

      sttWs.onopen = () => {
        console.log('[STT] Connected successfully');
        setState(prev => ({ ...prev, isConnected: true, error: null }));
      };

      sttWs.onmessage = (event) => {
        console.log('[STT] Received message:', event.data);
        try {
          const data = JSON.parse(event.data);
          if (data.transcript) {
            const newTranscript = transcriptRef.current + ' ' + data.transcript;
            transcriptRef.current = newTranscript;
            setState(prev => ({ ...prev, transcript: newTranscript }));
          }
        } catch (e) {
          console.error('[STT] Failed to parse message:', e);
        }
      };

      sttWs.onerror = (error) => {
        console.error('[STT] WebSocket error:', error);
        setState(prev => ({ ...prev, error: 'STT connection error', isConnected: false }));
      };

      sttWs.onclose = (event) => {
        console.log('[STT] Disconnected, code:', event.code, 'reason:', event.reason);
        setState(prev => ({ 
          ...prev, 
          isConnected: false,
          error: event.code !== 1000 ? `Connection closed: ${event.reason || event.code}` : null
        }));
      };

      // Connect TTS WebSocket
      const ttsWs = new WebSocket(`${wsBaseUrl}?type=tts&sessionId=${sessionId}`);
      ttsWsRef.current = ttsWs;

      ttsWs.onopen = () => {
        console.log('[TTS] Connected');
      };

      ttsWs.onmessage = async (event) => {
        // Play audio from Sarvam TTS
        if (event.data instanceof Blob) {
          const audioBuffer = await event.data.arrayBuffer();
          playAudio(audioBuffer);
        }
      };

      ttsWs.onerror = (error) => {
        console.error('[TTS] Error:', error);
      };

      ttsWs.onclose = () => {
        console.log('[TTS] Disconnected');
      };

    } catch (error) {
      console.error('[Voice] Connection error:', error);
      setState(prev => ({ ...prev, error: 'Failed to connect' }));
    }
  }, [sessionId]);

  const playAudio = async (audioBuffer: ArrayBuffer) => {
    if (!audioContextRef.current) {
      audioContextRef.current = new AudioContext();
    }

    const audioCtx = audioContextRef.current;
    const buffer = await audioCtx.decodeAudioData(audioBuffer);
    const source = audioCtx.createBufferSource();
    source.buffer = buffer;
    source.connect(audioCtx.destination);
    source.start();
  };

  const startRecording = useCallback(async () => {
    try {
      console.log('[Voice] Requesting microphone access...');
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          sampleRate: 16000,
          channelCount: 1,
          echoCancellation: true,
          noiseSuppression: true,
        } 
      });

      console.log('[Voice] Microphone access granted');

      // Check for supported MIME types
      const mimeType = MediaRecorder.isTypeSupported('audio/webm') 
        ? 'audio/webm' 
        : 'audio/ogg';

      console.log('[Voice] Using MIME type:', mimeType);

      const mediaRecorder = new MediaRecorder(stream, { mimeType });
      mediaRecorderRef.current = mediaRecorder;

      mediaRecorder.ondataavailable = async (event) => {
        console.log('[Voice] Audio chunk available, size:', event.data.size, 'WS state:', sttWsRef.current?.readyState);

        if (event.data.size > 0 && sttWsRef.current?.readyState === WebSocket.OPEN) {
          // Send raw audio blob directly
          sttWsRef.current.send(event.data);
        } else if (sttWsRef.current?.readyState !== WebSocket.OPEN) {
          console.warn('[Voice] WebSocket not open, state:', sttWsRef.current?.readyState);
        }
      };

      mediaRecorder.onerror = (error) => {
        console.error('[Voice] MediaRecorder error:', error);
      };

      mediaRecorder.start(250); // Send chunks every 250ms
      console.log('[Voice] Recording started');
      setState(prev => ({ ...prev, isRecording: true }));
    } catch (error) {
      console.error('[Voice] Microphone error:', error);
      setState(prev => ({ ...prev, error: 'Microphone access denied' }));
    }
  }, []);

  const stopRecording = useCallback(() => {
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setState(prev => ({ ...prev, isRecording: false }));
    }
  }, []);

  const sendTextToSpeech = useCallback((text: string) => {
    if (ttsWsRef.current?.readyState === WebSocket.OPEN) {
      ttsWsRef.current.send(JSON.stringify({ 
        action: 'speak',
        text 
      }));
    }
  }, []);

  const disconnect = useCallback(() => {
    stopRecording();
    sttWsRef.current?.close();
    ttsWsRef.current?.close();
    audioContextRef.current?.close();

    sttWsRef.current = null;
    ttsWsRef.current = null;
    audioContextRef.current = null;
    transcriptRef.current = '';

    setState({
      isConnected: false,
      isRecording: false,
      transcript: '',
      error: null,
    });
  }, [stopRecording]);

  useEffect(() => {
    return () => {
      disconnect();
    };
  }, [disconnect]);

  return {
    ...state,
    connect,
    disconnect,
    startRecording,
    stopRecording,
    sendTextToSpeech,
    fullTranscript: transcriptRef.current,
  };
}

server/services/sarvam.ts.content:
import WebSocket from 'ws';

const SARVAM_API_KEY = process.env.SARVAM_API_KEY;
const SARVAM_BASE_URL = 'https://api.sarvam.ai';

if (!SARVAM_API_KEY) {
  console.warn('SARVAM_API_KEY not found in environment variables');
}

// Chat completion with Sarvam
export async function chatCompletion(messages: Array<{ role: string; content: string }>, temperature = 0.3) {
  const response = await fetch(`${SARVAM_BASE_URL}/chat/completions`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'api-subscription-key': SARVAM_API_KEY || '',
    },
    body: JSON.stringify({
      model: 'sarvam-2b',
      messages,
      temperature,
    }),
  });

  if (!response.ok) {
    const error = await response.text();
    throw new Error(`Sarvam chat API error: ${response.status} ${error}`);
  }

  return response.json();
}

// Create WebSocket connection to Sarvam STT
export function createSTTWebSocket(language = 'hi-IN'): WebSocket {
  // Build WebSocket URL with query parameters
  const params = new URLSearchParams({
    'language-code': language,
    'model': 'saarika:v2.5',
    'vad_signals': 'true',
    'sample_rate': '16000',
  });

  const ws = new WebSocket(`wss://api.sarvam.ai/speech-to-text/ws?${params.toString()}`, {
    headers: {
      'Api-Subscription-Key': SARVAM_API_KEY || '',
    },
  });

  ws.on('open', () => {
    console.log('[Sarvam STT] WebSocket connected');
  });

  return ws;
}

// Create WebSocket connection to Sarvam TTS
export function createTTSWebSocket(
  language = 'hi-IN',
  speaker = 'meera',
  model = 'bulbul:v2'
): WebSocket {
  // Build WebSocket URL with query parameters
  const params = new URLSearchParams({
    'model': model,
    'send_completion_event': 'true',
  });

  const ws = new WebSocket(`wss://api.sarvam.ai/text-to-speech/ws?${params.toString()}`, {
    headers: {
      'Api-Subscription-Key': SARVAM_API_KEY || '',
    },
  });

  // Send configuration on connection
  ws.on('open', () => {
    console.log('[Sarvam TTS] WebSocket connected');
    ws.send(JSON.stringify({
      action: 'configure',
      target_language_code: language,
      speaker,
    }));
  });

  return ws;
}

// Generate end-of-call summary using Sarvam chat
export async function generateCallSummary(transcript: string): Promise<{
  partnerTypeOneLiner: string;
  top3TraitsYouValue: string[];
  whatYouMightWorkOn: string[];
  nextTimeFocus: string[];
  loveLanguageGuess: string;
  communicationFit: string;
  confidenceScore: number;
}> {
  const summaryPrompt = `Analyze this conversation transcript and provide a relationship profile summary in JSON format.

Transcript:
${transcript}

Provide a JSON response with:
- partnerTypeOneLiner: A one-line description of their ideal partner type
- top3TraitsYouValue: Array of 3 traits they value most in a partner
- whatYouMightWorkOn: Array of 2-3 areas for personal growth
- nextTimeFocus: Array of 2-3 topics to explore in next conversation
- loveLanguageGuess: Their likely love language (words of affirmation, quality time, physical touch, acts of service, or receiving gifts)
- communicationFit: Assessment of their communication style
- confidenceScore: Confidence in this analysis (0-1 scale)

Return only valid JSON, no other text.`;

  const response = await chatCompletion([
    { role: 'system', content: 'You are an expert relationship analyst. Always respond with valid JSON only.' },
    { role: 'user', content: summaryPrompt },
  ], 0.3);

  const content = response.choices[0]?.message?.content || '{}';

  try {
    const summary = JSON.parse(content);
    return {
      partnerTypeOneLiner: summary.partnerTypeOneLiner || '',
      top3TraitsYouValue: summary.top3TraitsYouValue || [],
      whatYouMightWorkOn: summary.whatYouMightWorkOn || [],
      nextTimeFocus: summary.nextTimeFocus || [],
      loveLanguageGuess: summary.loveLanguageGuess || '',
      communicationFit: summary.communicationFit || '',
      confidenceScore: summary.confidenceScore || 0.5,
    };
  } catch (error) {
    console.error('Failed to parse summary JSON:', error);
    throw new Error('Failed to generate call summary');
  }
}

