import axios from 'axios';

const CASHFREE_APP_ID = process.env.CASHFREE_APP_ID;
const CASHFREE_SECRET_KEY = process.env.CASHFREE_SECRET_KEY;

// Use sandbox for testing, change to production URL when going live
const CASHFREE_BASE_URL = 'https://sandbox.cashfree.com/pg';

interface CreateOrderParams {
  orderId: string;
  orderAmount: number;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  returnUrl: string;
}

interface CashfreeOrderResponse {
  cf_order_id: number;
  order_id: string;
  order_token: string;
  payment_session_id: string;
  order_status: string;
}

export async function createCashfreeOrder(params: CreateOrderParams): Promise<CashfreeOrderResponse> {
  try {
    const response = await axios.post(
      `${CASHFREE_BASE_URL}/orders`,
      {
        order_id: params.orderId,
        order_amount: params.orderAmount,
        order_currency: 'INR',
        customer_details: {
          customer_id: params.customerEmail,
          customer_name: params.customerName,
          customer_email: params.customerEmail,
          customer_phone: params.customerPhone,
        },
        order_meta: {
          return_url: params.returnUrl,
        },
      },
      {
        headers: {
          'x-client-id': CASHFREE_APP_ID,
          'x-client-secret': CASHFREE_SECRET_KEY,
          'x-api-version': '2023-08-01',
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error: any) {
    console.error('Cashfree create order error:', error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to create Cashfree order');
  }
}

interface CashfreePaymentStatus {
  cf_order_id: number;
  order_id: string;
  order_amount: number;
  order_currency: string;
  order_status: string;
  payment_session_id: string;
  cf_payment_id?: number;
  payment_amount?: number;
  payment_status?: string;
  payment_time?: string;
}

export async function getCashfreePaymentStatus(orderId: string): Promise<CashfreePaymentStatus> {
  try {
    const response = await axios.get(
      `${CASHFREE_BASE_URL}/orders/${orderId}`,
      {
        headers: {
          'x-client-id': CASHFREE_APP_ID,
          'x-client-secret': CASHFREE_SECRET_KEY,
          'x-api-version': '2023-08-01',
        },
      }
    );
    return response.data;
  } catch (error: any) {
    console.error('Cashfree payment status error:', error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to get payment status');
  }
}

