import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ArrowLeft, Heart, MessageCircle, Target, TrendingUp, Brain, Award, AlertTriangle } from 'lucide-react';
import { Link } from 'wouter';
import { Skeleton } from '@/components/ui/skeleton';
import { useAuth } from '@/contexts/AuthContext';
import { apiRequest } from '@/lib/queryClient';

interface SummaryResponse {
  hasSummary: boolean;
  message?: string;
  summary?: {
    partnerTypeOneLiner: string | null;
    top3TraitsYouValue: string[] | null;
    whatYouMightWorkOn: string[] | null;
    nextTimeFocus: string[] | null;
    loveLanguageGuess: string | null;
    communicationFit: string | null;
    confidenceScore: string | null;
    updatedAt: string;
  };
}

export default function SummaryPage() {
  const { user } = useAuth();

  const { data, isLoading, isError, error, refetch } = useQuery<SummaryResponse>({
    queryKey: ['/api/summary'],
    queryFn: async () => {
      const response = await apiRequest("POST", "/api/summary", {});
      return response.json();
    },
    enabled: !!user,
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <header className="fixed top-0 left-0 right-0 h-16 bg-background/95 backdrop-blur-sm border-b border-border z-10">
          <div className="max-w-4xl mx-auto h-full flex items-center px-4">
            <Link href="/">
              <Button variant="ghost" size="sm" className="gap-2" data-testid="button-back-to-chat">
                <ArrowLeft className="w-4 h-4" />
                Back to Chat
              </Button>
            </Link>
          </div>
        </header>

        <main className="pt-20 pb-8 px-4">
          <div className="max-w-4xl mx-auto space-y-6">
            <Skeleton className="h-32 w-full" />
            <Skeleton className="h-48 w-full" />
            <Skeleton className="h-48 w-full" />
          </div>
        </main>
      </div>
    );
  }

  if (isError) {
    return (
      <div className="min-h-screen bg-background">
        <header className="fixed top-0 left-0 right-0 h-16 bg-background/95 backdrop-blur-sm border-b border-border z-10">
          <div className="max-w-4xl mx-auto h-full flex items-center px-4">
            <Link href="/">
              <Button variant="ghost" size="sm" className="gap-2" data-testid="button-back-to-chat">
                <ArrowLeft className="w-4 h-4" />
                Back to Chat
              </Button>
            </Link>
          </div>
        </header>

        <main className="pt-20 pb-8 px-4">
          <div className="max-w-4xl mx-auto flex flex-col items-center justify-center min-h-[60vh] text-center">
            <AlertTriangle className="w-16 h-16 text-destructive mb-4" />
            <h2 className="text-2xl font-bold mb-2" data-testid="text-error-title">Unable to Load Summary</h2>
            <p className="text-muted-foreground mb-6 max-w-md" data-testid="text-error-message">
              {error instanceof Error ? error.message : "An error occurred while fetching your summary. Please try again."}
            </p>
            <div className="flex gap-3">
              <Link href="/">
                <Button variant="outline" data-testid="button-back-home">Back to Chat</Button>
              </Link>
              <Button onClick={() => refetch()} data-testid="button-retry">Try Again</Button>
            </div>
          </div>
        </main>
      </div>
    );
  }

  if (!data?.hasSummary) {
    return (
      <div className="min-h-screen bg-background">
        <header className="fixed top-0 left-0 right-0 h-16 bg-background/95 backdrop-blur-sm border-b border-border z-10">
          <div className="max-w-4xl mx-auto h-full flex items-center px-4">
            <Link href="/">
              <Button variant="ghost" size="sm" className="gap-2" data-testid="button-back-to-chat">
                <ArrowLeft className="w-4 h-4" />
                Back to Chat
              </Button>
            </Link>
          </div>
        </header>

        <main className="pt-20 pb-8 px-4">
          <div className="max-w-4xl mx-auto flex flex-col items-center justify-center min-h-[60vh] text-center">
            <Brain className="w-16 h-16 text-muted-foreground mb-4" />
            <h2 className="text-2xl font-bold mb-2" data-testid="text-no-summary-title">No Summary Yet</h2>
            <p className="text-muted-foreground mb-6 max-w-md" data-testid="text-no-summary-message">
              {data?.message || "Complete a conversation to generate your first relationship insights summary."}
            </p>
            <div className="flex gap-3">
              <Link href="/">
                <Button variant="outline" data-testid="button-start-chatting">Start Chatting</Button>
              </Link>
              <Link href="/call">
                <Button data-testid="button-start-call">Start Voice Call</Button>
              </Link>
            </div>
          </div>
        </main>
      </div>
    );
  }

  const summary = data.summary!;
  const confidencePercent = summary.confidenceScore ? Math.round(parseFloat(summary.confidenceScore) * 100) : 0;

  return (
    <div className="min-h-screen bg-background">
      <header className="fixed top-0 left-0 right-0 h-16 bg-background/95 backdrop-blur-sm border-b border-border z-10">
        <div className="max-w-4xl mx-auto h-full flex items-center justify-between px-4">
          <Link href="/">
            <Button variant="ghost" size="sm" className="gap-2" data-testid="button-back-to-chat">
              <ArrowLeft className="w-4 h-4" />
              Back to Chat
            </Button>
          </Link>
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <Award className="w-4 h-4" />
            <span data-testid="text-confidence-score">{confidencePercent}% Confidence</span>
          </div>
        </div>
      </header>

      <main className="pt-20 pb-8 px-4">
        <div className="max-w-4xl mx-auto space-y-6">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold mb-2" data-testid="text-page-title">Your Relationship Insights</h1>
            <p className="text-muted-foreground" data-testid="text-page-subtitle">
              Based on your conversations with Riya
            </p>
          </div>

          {summary.partnerTypeOneLiner && (
            <Card data-testid="card-partner-type">
              <CardHeader>
                <div className="flex items-center gap-2">
                  <Heart className="w-5 h-5 text-primary" />
                  <CardTitle>Your Ideal Partner</CardTitle>
                </div>
                <CardDescription>What you're looking for in a relationship</CardDescription>
              </CardHeader>
              <CardContent>
                <p className="text-lg" data-testid="text-partner-type">{summary.partnerTypeOneLiner}</p>
              </CardContent>
            </Card>
          )}

          {summary.top3TraitsYouValue && summary.top3TraitsYouValue.length > 0 && (
            <Card data-testid="card-traits">
              <CardHeader>
                <div className="flex items-center gap-2">
                  <Target className="w-5 h-5 text-primary" />
                  <CardTitle>Top 3 Traits You Value</CardTitle>
                </div>
                <CardDescription>Qualities that matter most to you</CardDescription>
              </CardHeader>
              <CardContent>
                <ul className="space-y-2">
                  {summary.top3TraitsYouValue.map((trait, index) => (
                    <li key={index} className="flex items-start gap-2" data-testid={`text-trait-${index}`}>
                      <span className="text-primary font-semibold">{index + 1}.</span>
                      <span>{trait}</span>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          )}

          <div className="grid md:grid-cols-2 gap-6">
            {summary.whatYouMightWorkOn && summary.whatYouMightWorkOn.length > 0 && (
              <Card data-testid="card-work-on">
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <TrendingUp className="w-5 h-5 text-primary" />
                    <CardTitle>Growth Areas</CardTitle>
                  </div>
                  <CardDescription>Things to work on</CardDescription>
                </CardHeader>
                <CardContent>
                  <ul className="space-y-2">
                    {summary.whatYouMightWorkOn.map((item, index) => (
                      <li key={index} className="text-sm" data-testid={`text-work-on-${index}`}>
                        • {item}
                      </li>
                    ))}
                  </ul>
                </CardContent>
              </Card>
            )}

            {summary.nextTimeFocus && summary.nextTimeFocus.length > 0 && (
              <Card data-testid="card-next-focus">
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <MessageCircle className="w-5 h-5 text-primary" />
                    <CardTitle>Next Time Focus</CardTitle>
                  </div>
                  <CardDescription>Topics to explore</CardDescription>
                </CardHeader>
                <CardContent>
                  <ul className="space-y-2">
                    {summary.nextTimeFocus.map((item, index) => (
                      <li key={index} className="text-sm" data-testid={`text-next-focus-${index}`}>
                        • {item}
                      </li>
                    ))}
                  </ul>
                </CardContent>
              </Card>
            )}
          </div>

          {(summary.loveLanguageGuess || summary.communicationFit) && (
            <Card data-testid="card-insights">
              <CardHeader>
                <div className="flex items-center gap-2">
                  <Brain className="w-5 h-5 text-primary" />
                  <CardTitle>Additional Insights</CardTitle>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                {summary.loveLanguageGuess && (
                  <div>
                    <h4 className="font-semibold mb-1">Love Language Guess</h4>
                    <p className="text-muted-foreground" data-testid="text-love-language">{summary.loveLanguageGuess}</p>
                  </div>
                )}
                {summary.communicationFit && (
                  <div>
                    <h4 className="font-semibold mb-1">Communication Style</h4>
                    <p className="text-muted-foreground" data-testid="text-communication-fit">{summary.communicationFit}</p>
                  </div>
                )}
              </CardContent>
            </Card>
          )}

          <div className="text-center text-sm text-muted-foreground pt-4" data-testid="text-updated-at">
            Last updated: {new Date(summary.updatedAt).toLocaleDateString('en-US', { 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
        </div>
      </main>
    </div>
  );
}

